$(document).ready(function () {

    /*mobile menu trigger*/
    $(document).on('click', '#mobile-trigger', function (event) {
        event.preventDefault();
        var menu = $(this).closest('.header__navigation').find('.header__nav');
        if ($(this).hasClass('js-trigger-active')) {
            $(this).removeClass('js-trigger-active');
            menu.slideUp('slow');
        } else {
            $(this).addClass('js-trigger-active');
            menu.slideDown('slow');
        }
        return false;
    });
    if (window.innerWidth < 756) {
        $(document).mouseup(function (event) {
            var block = $('.header__navigation');
            if (!block.is(event.target)
                && block.has(event.target).length === 0) {
                $('#mobile-trigger').removeClass('js-trigger-active');
                $('.header__nav').slideUp('500');
            }
        });
    }
    /*close*/

    /*main banner slider*/
    $('#main-banner').slick({
        dots: true,
        arrows: false,
        autoplay: true,
        autoplaySpeed: 2000,
        infinite: true,
        speed: 500,
        fade: true,
        cssEase: 'linear'
    });
    /*close*/

    /*category change*/
    $(document).on('change', '#category', function() {
        var selected = $(this).val();

        $.ajax({
            url: myajax.url,
            type: 'POST',
            data: {
                action: 'myajax',
                postID: selected
            },
            beforeSend: function(xhr) {},
            success: function(data) {
                console.log('Success');
                // $('.card-item-1').html(data);
            },
            error: function() {
                console.log('Error');
            }
        });
    });
    /*category change*/

});